// @flow

class TaskItem {
    id: string;
    value: string;
    createdOn: Date;
    closed: bool;
    updatedOn: Date;
    constructor(id: string, value: string, createdOn: Date, updatedOn: Date) {
        this.id = id;
        this.value = value;
        this.createdOn = createdOn;
        this.updatedOn = updatedOn;
        this.closed = false; // テーブルで選択状態を表現するのに利用
    }
}

export default TaskItem;